import { Card } from "react-bootstrap";
import React, { useState } from 'react'
//import {Link} from 'react-router-dom'
import "./Dashboard.css";
const CardTemplate = (props) => {
  const [hoverColor, setHoverColor] = useSate('green');
 const handleMouseOver =() =>{
   setHoverColor('white')
 }

  return (
    <Card className="individualCard" style={{color:hoverColor}} onMouseOver = {handleMouseOver}>
      <Card.Body>
        <div className = "cardbody">
        <p className="cardTitle">
          {props.cardName}
          {props.lock && (
            <img
              src={props.lock}
              style={{
                position:'absolute',
                height: "20px",
                top:'8%',
                right:"6%"
              }}
            ></img>
          )}
        </p>

        <div className="image">
          <img src={props.cardIcon} className="icon"></img>
        </div>

        {props.thirdCard && (
          <div class="row">
            <div className="col left">
              <p className="Text">Task Available</p>
              <h4 className="Count">72</h4>
            </div>
            <div style={{ textAlign: "right" }} className="col">
              <p className="Text">Requests Available</p>
              <h4 className="Count">50</h4>
            </div>
          </div>
        )}

        {props.identify && (
          <div class="row">
            <div className="col left">
              <p className="Text">Open</p>
              <h4 className="Count">72</h4>
            </div>
            <div style={{ textAlign: "right" }} className="col">
              <p className="Text">Drafts</p>
              <h4 className="Count">50</h4>
            </div>
          </div>
        )}
        {props.lastCard && (
          <div className = "lastCardlines">
            <h2 style = {{position:'absolute',top:'2%'}}>hello</h2>

            </div>
        )}
        </div>
      </Card.Body>
    </Card>
  );
};

export default CardTemplate;
