import { Card } from "react-bootstrap";
import React, { useState } from 'react'
//import {Link} from 'react-router-dom'
import "./Dashboard.css";
const CardTemplate = (props) => {
  const [hoverColor, setHoverColor] = useState('#348fe2');
 const handleMouseOver =() =>{
   setHoverColor('white');
   
 }
 const handleMouseLeave = ()=>{
   setHoverColor('#348fe2');
 }

 const [hoverCountColor, setHoverCountColor] = useState('#629C44');
 const handleMouseCountOver = () =>{
   setHoverCountColor('white');
 }
 const handleMouseCountLeave = () =>{
     setHoverCountColor('#629C44');
 }

  return (
    <Card className="individualCard" style={{color:hoverColor}} onMouseOver = {handleMouseOver} onMouseLeave = {handleMouseLeave} onMouseCountOver = {handleMouseCountOver} onMouseCountLeave = {handleMouseCountLeave}>
      <Card.Body>
       
        <p className="cardTitle" style={{color:hoverColor}}>
          {props.cardName}
          {props.lock && (
            <img
              src={props.lock}
              style={{
                position:'absolute',
                height: "20px",
                top:'8%',
                right:"6%"
              }}
            ></img>
          )}
        </p>

        {!props.lastCard ?
          <div className="image">
          <img src={props.cardIcon} className="icon"></img>
        </div>
        :""
        }

        {props.thirdCard && (
          <div class="row">
            <div className="col left">
              <p className="Text" style={{color:hoverColor}}>Task Available</p>
              <h4 className="Count" style={{color:hoverCountColor}} >72</h4>
            </div>
            <div style={{ textAlign: "right" }} className="col">
              <p className="Text"style={{color:hoverColor}}>Requests Available</p>
              <h4 className="Count">50</h4>
            </div>
          </div>
        )}

        {props.identify && (
          <div class="row">
            <div className="col left">
              <p className="Text" style={{color:hoverColor}}>Open</p>
              <h4 className="Count">72</h4>
            </div>
            <div style={{ textAlign: "right" }} className="col">
              <p className="Text" style={{color:hoverColor}}>Drafts</p>
              <h4 className="Count">50</h4>
            </div>
          </div>
        )}
        {props.lastCard && (
          <div >
          <div className="row">
            <p style ={{position:'absolute',top:'18px',left:'100px',fontSize:'25px'}}>Need Help?</p>
          </div>
          <br/>
          <br/>
          <div className="row">
            <b style ={{textAlign : 'center', color:'#629C44'}}>Genesys Support</b>
            <b style ={{textAlign : 'center', color:'black'}}>Contact@nvknurseries.com</b>
          </div>
          <br />
          <div className="row">
            <b style ={{textAlign : 'center', color:'#629C44'}}>Human Resources</b>
            <b style ={{textAlign : 'center', color:'black'}}>Contact@nvknurseries.com</b>
          </div>
          <br />
         
            <div style ={{fontSize : '15px', fontWeight : 'bold', textAlign:'center',color:'black'}} className="col">
              Staff Directory &nbsp;
              <a style={{ textAlign: "right" }} className="col">
              Click Here
            </a>
            </div>
            <div style={{textAlign:'right'}}>
            <span class="editIcon" ><a href=""><img src="./assets/img/edit-ic.svg"/></a></span> 
            </div>        
        </div>
        )}
      </Card.Body>
    </Card>
  );
};

export default CardTemplate;
