import { Card } from "react-bootstrap";
import React, { useState } from "react";
//import {Link} from 'react-router-dom'
import "./Dashboard.css";
const CardTemplate = (props) => {
  //   const [hoverColor, setHoverColor] = useState('#348fe2');
  //  const handleMouseOver =() =>{
  //    setHoverColor('white');

  //  }
  //  const handleMouseLeave = ()=>{
  //    setHoverColor('#348fe2');
  //  }

  //  const [hoverCountColor, setHoverCountColor] = useState('#629C44');
  //  const handleMouseCountOver = () =>{
  //    setHoverCountColor('white');
  //  }
  //  const handleMouseCountLeave = () =>{
  //      setHoverCountColor('#629C44');
  //  }

  return (
    <Card className="individualCard">
      <Card.Body className="cardBody">
        <div>
          <p>
          {props.cardName}
          {props.lock && (
            <img
              src={props.lock}
              style={{
                position: "absolute",
                height: "20px",
                top: "8%",
                right: "6%",
              }}
            ></img>
          )}
          </p>
         
        {props.identify && (
          <div class="row">
            
              <p>Open</p>
              <h4>72</h4>
           
            
              <p>Drafts</p>
              <h4>50</h4>
          
          </div>
        )}

        {props.thirdCard && (
          <div class="row">
            <div>
              <p>Task Available</p>
              <h4>72</h4>
            </div>
            <div className="col">
              <p>Requests Available</p>
              <h4>50</h4>
            </div>
          </div>
        )}
         {!props.lastCard ? (
          <div>
            <img src={props.cardIcon}></img>
          </div>
        ) : (
          ""
        )}

        {props.lastCard && (
          <div>
            <div className="row">
              <p
                style={{
                  position: "absolute",
                  top: "16px",
                  left: "120px",
                  fontSize: "25px",
                }}
              >
                Need Help?
              </p>
            </div>
            <br />
            <br />
            <div className="row">
              <b style={{ textAlign: "center", color: "#629C44" }}>
                Genesys Support
              </b>
              <b style={{ textAlign: "center", color: "black" }}>
                Contact@nvknurseries.com
              </b>
            </div>
            <br />
            <div className="row">
              <b style={{ textAlign: "center", color: "#629C44" }}>
                Human Resources
              </b>
              <b style={{ textAlign: "center", color: "black" }}>
                Contact@nvknurseries.com
              </b>
            </div>
            <br />

            <div
              style={{
                fontSize: "15px",
                fontWeight: "bold",
                textAlign: "center",
                color: "black",
              }}
              className="col"
            >
              Staff Directory &nbsp;
              <a style={{ textAlign: "right" }} className="col">
                Click Here
              </a>
            </div>
            <div style={{ textAlign: "right" }}>
              <span class="editIcon">
                <a href="">
                  <img src="./assets/img/edit-ic.svg" />
                </a>
              </span>
            </div>
          </div>
           )}
        </div>

        
       
      </Card.Body>
    </Card>
  );
};

export default CardTemplate;
